import React, { useState, useEffect } from "react";

const Looper = () => {
  const messages = ["I am a student", "I am a developer", "I am a boy"];
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % messages.length);
    }, 2000); // Change the interval (in milliseconds) as needed

    return () => clearInterval(intervalId); // Cleanup function to stop interval on unmount
  }, [messages.length]); // Only run effect when messages length changes

  return (
    <div className="home">
      <h1>Welcome!</h1>
      <p>{messages[currentIndex]}</p>
    </div>
  );
};

export default Looper;
