import React, { useState, useEffect } from "react";

const Looper = () => {
  const messages = ["I am a student", "I am a developer", "I am a boy"];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentText, setCurrentText] = useState(messages[currentIndex]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      let newText = currentText.slice(0, currentText.length - 1); // Backspace the last character
      setCurrentText(newText);
      if (newText.length === 0) {
        // If all characters are backspaced
        const newIndex = (currentIndex + 1) % messages.length;
        setCurrentIndex(newIndex);
        setCurrentText(messages[newIndex]);
      }
    }, 50); // Adjust interval for animation speed (lower = faster)

    return () => clearInterval(intervalId);
  }, [currentIndex, messages.length]);

  return (
    <div className="home">
      <h1>Welcome!</h1>
      <p className="animatedText">{currentText}</p>
    </div>
  );
};

export default Looper;
